import React, { useState } from 'react';

const BuyerRegistration = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword:'',
    profileImage: null,
    phoneNumber: '',
    address: '',
    creditCardNumber: ''
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setFormData((prevData) => ({
      ...prevData,
      profileImage: file,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Implement logic to handle form submission (e.g., send data to the server)
    console.log('Form submitted:', formData);
  };

  // Styles remain unchanged...

  return (
    <form style={formStyle} onSubmit={handleSubmit}>
      <h2 style={{alignContent:'center'}}>Welcome to Customer Panel</h2>
      {/* Existing input fields for name, email, and passwords */}
      <label style={labelStyle}>
        Phone Number:
        <input type="text" name="phoneNumber" value={formData.phoneNumber} onChange={handleInputChange} style={inputStyle} />
      </label>
      <label style={labelStyle}>
        Address:
        <input type="text" name="address" value={formData.address} onChange={handleInputChange} style={inputStyle} />
      </label>
      <label style={labelStyle}>
        Credit Card Number:
        <input type="text" name="creditCardNumber" value={formData.creditCardNumber} onChange={handleInputChange} style={inputStyle} />
      </label>
      {/* Existing input fields for profile image and submit button */}
    </form>
  );
};

export default BuyerRegistration;
